function stimParams = SurroundParams_fMRIstimParams.regions = 8;stimParams.size = [3 6];                % inner radius, outer radius (degrees of visual angle)                                            % (from Zenger-Landolt and Heeger, 2003: [4.5 7.8])stimParams.surroundSize = [0.4 9]; %[0.4 10.5] for NNL;     % radius (from Zenger-Landolt and Heeger, 2003: 16.4)stimParams.RingWidth = .1;                  % degrees of visual anglestimParams.SpokeWidth = .1;                 % degrees of visual anglestimParams.StaRange = 1;     				% color map value reserved for rings and spokesstimParams.spatialFrequency = 1.1;          % cycles/deg (from Zenger-Landolt and Heeger, 2003)stimParams.spatialPhase = 0;stimParams.temporalFrequency = 4;           % Hz (from Zenger-Landolt and Heeger, 2003)stimParams.temporalPhase = 0;       		% secondsstimParams.stimulusDuration = 0.75;         % seconds (from Zenger-Landolt and Heeger, 2003)                        %stimParams.responseDuration = .9; %0.8;     % 3T scanner: TR = 1.34, block dur = 10.72, trial dur should be 2.144stimParams.responseDuration = 1;stimParams.feedbackDuration = 0.25; %0.25;         % secondsstimParams.scanner = 3;                     % 1 for psychophysics; 2 for Varian scanner; 3 for NNL goggles; 4 for Jong's laptop; 5 for Avotec projectorid = inputdlg('Subject''s initials'); stimParams.id = id{1};   %;			    	% Subject's initialsclear id;stimParams.numberBlocks = 16;      %each block is 5 trials in alternation with blank (annulus frame and surround, no annulus gratings)                                     %with block duration = 10.72 and dummy                                    %trials, total scan duration = 10.72*33 = 353.76 seconds                                    %264 TRs at 1.34 seconds per TR (plus 2 to check timing)stimParams.trialDuration = stimParams.stimulusDuration + stimParams.responseDuration + stimParams.feedbackDuration;stimParams.blockDuration = stimParams.trialDuration*5; % 5 trials per blockstimParams.library = 0;                      % 1 means use stimuli loaded from library, 0 means build them from scratchstimParams.pedestalContrast = 0.75; %low-contrast segment is variable and determined by staircase,%other seven segments are fixed at pedestal contraststimParams.contrastIncrements = 15;         %15 is a generally good number to use for subjectsstimParams.surroundContrast = 1;stimParams.feedback = 1;stimParams.dummyScans = 1;                  % Add one block (six trials) of dummy scans%%%%%%stimParams.nDown = 3;			%number of correct trials needed to decrease contrast one step in staircase%%% Fixation point parameters %%%stimParams.fixationSize = .3;              % degreesstimParams.type = 'black_square';                 % fixation shapestimParams.contrastRange = [.3 stimParams.pedestalContrast];                                          stimParams.fixLum = 12; %luminance of target on a black fixpoint (0 = black, 255 = white)stimParams.fixSizeRange = [1 7];     % in pixelsstimParams.sizeIncrements = 7; %should allow for integral number of pixels in targetstimParams.constantFixSize = 3; %these are the fixation "targets" that are shown during the attend to annulus scans% 1 is no surround; 2 is parallel surround; 3 is orthogonal surround (all attend to annulus)% 4 is no surround; 5 is parallel surround; 6 is orthogonal surround (all attend to fix)% 7 is practice fix task (no contrast decrements in annulus and no% surround)ButtonName = questdlg('Which surround?', ...    'Task condition', ...    'None', 'Para', 'Ortho', 'None');switch ButtonName,    case 'None',        stimParams.scanSequence = [1 4];    case 'Para',        stimParams.scanSequence = [2 5];    case 'Ortho',        stimParams.scanSequence = [3 6];end % switchstimParams.numberScans = length(stimParams.scanSequence);